package day1;import java.util.HashMap;import java.util.Map;public class TwoSum {    public int[] twoSum(int[] nums, int target) {        Map<Integer, Integer> map = new HashMap<>();        for (int index = 0; index < nums.length; index++) {            int difference = target - nums[index];            if (map.containsKey(nums[index])) {                return new int[]{map.get(nums[index]), index};            }            map.put(difference, index);        }        return null;    }}