package day1;import java.util.HashMap;import java.util.Map;public class TwoSum {    public int[] twoSum(int[] number, int target) {        Map<Integer, Integer> map = new HashMap<>();        for (int index = 0; index < number.length; index++) {            int difference = target - number[index];            if (map.containsKey(number[index])) {                return new int[]{map.get(number[index]), index};            }            map.put(difference, index);        }        return null;    }}