package day4;public class LongestCommonPrefix {    public String checkCommonPrefix(String[] str){        String emptyString = "";        if (str.length == 0) {            return emptyString;        }        String prefix = str[0];        for (int index = 1; index < str.length ; index++) {            int minLength = Math.min(prefix.length(),str[index].length());            int counter = 0;            while (counter < minLength && prefix.charAt(counter) == str[index].charAt(counter)){                counter++;            }            prefix = prefix.substring(0,counter);            if (prefix.isEmpty()){                break;            }        }        return prefix;    /*    }        return Stream.of(str)                .min((str1,str2) -> {                    int minLength = Math.min(str1.length(),str2.length());                    int index = 0;                    while (index < minLength && str1.charAt(index) == str2.charAt(index)){                        index++;                    }                    return minLength -index;                }).map(string -> {                    int minLength = Math.min(string.length(),)                })*/        /*.map(string -> string.substring(0,string.length() - (minLength - index)))                .stream().findFirst().orElseThrow();*/    }}