package day4;import java.util.HashMap;public class RomanToInteger {    private static final HashMap<String,Integer> valueOfRomanNumeral = new HashMap<>();    static {        valueOfRomanNumeral.put("I", 1);        valueOfRomanNumeral.put("V", 5);        valueOfRomanNumeral.put("X", 10);        valueOfRomanNumeral.put("L", 50);        valueOfRomanNumeral.put("C", 100);        valueOfRomanNumeral.put("D", 500);        valueOfRomanNumeral.put("M", 1000);    }    public static int convertRomanToInteger(String romanNumeral){        if (romanNumeral == null || romanNumeral.isBlank() || romanNumeral.isEmpty()){            throw new IllegalArgumentException("Input string must not be null or empty");        }        int result = 0;        int previous = 0;        for (int index = romanNumeral.length()-1; index >= 0 ; index--) {            int current = valueOfRomanNumeral.get(romanNumeral.substring(index,index + 1));            if (previous < current){                result = result - previous;            }else {                previous = current;            }result = result + current;        }        return result;    }}