package day5;import java.util.*;public class ThreeSum {    public List<List<Integer>> threeSum(int[] nums) {        Set<List<Integer>> uniqueTriplets = new HashSet<>();        Arrays.sort(nums);        for (int index = 0; index < nums.length - 2; index++) {            int targetSum = -nums[index];            int left = index + 1;            int right = nums.length - 1;            while (left < right) {                int currentSum = nums[left] + nums[right];                if (currentSum == targetSum) {                    List<Integer> triplet = new ArrayList<>();                    triplet.add(nums[index]);                    triplet.add(nums[left]);                    triplet.add(nums[right]);                    uniqueTriplets.add(triplet);                    left++;                    right--;                } else if (currentSum < targetSum) {                    left++;                } else {                    right--;                }            }        }        List<List<Integer>> result = new ArrayList<>(uniqueTriplets);        return result;    }}