package day8;import java.util.HashSet;import java.util.Set;public class Checker {    public static boolean hasDuplicate(String str) {        str = str.toLowerCase();        Set<Character> uniqueChars = new HashSet<>();        for (char ch : str.toCharArray()) {            if (uniqueChars.contains(ch)) {                return true;            } else {                uniqueChars.add(ch);            }        }        return false;    }    public static void main(String[] args) {        String str1 = "This is a sentence with a duplicate word.";        String str2 = "This is a sentence without duplicates.";        System.out.println(str1 + " has duplicate words: " + hasDuplicate(str1));        System.out.println(str2 + " has duplicate words: " + hasDuplicate(str2));    }}