package day1;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.*;class LongestSubstringTest {    @DisplayName("test longest substring")    @Test    void testLongestSubstring(){        LongestSubstring substring = new LongestSubstring();        int actual = substring.longestSubstring("abcabcbb");        int expected = 3;        assertEquals(expected,actual);    }    @DisplayName("second test case")    @Test    void testLongestSubstring_2(){        LongestSubstring substring = new LongestSubstring();        int actual = substring.longestSubstring("bbbbb");        int expected = 1;        assertEquals(expected,actual);    }    @DisplayName("third test case")    @Test    void testLongestSubstring_3(){        LongestSubstring substring = new LongestSubstring();        int actual = substring.longestSubstring("pwwkew");        int expected = 3;        assertEquals(expected,actual);    }}