package day4;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.*;class LongestCommonPrefixTest {    @Test    void LongestCommonPrefixTest(){        LongestCommonPrefix commonPrefix = new LongestCommonPrefix();        String[] inputString = {"flower", "flow", "flight"};        String actual = commonPrefix.checkCommonPrefix(inputString);        Assertions.assertEquals("fl",actual);    }}