package day5;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;import java.util.ArrayList;import java.util.List;import static org.junit.jupiter.api.Assertions.assertEquals;class ThreeSumTest {    @Test    public void threeSumTest(){        ThreeSum sum = new ThreeSum();        int[] nums = {-1,0,1,2,-1,-4};        List<List<Integer>> actual = sum.threeSum(nums);        List<List<Integer>> expectedOutput = new ArrayList<>();        List<Integer> triplet1 = new ArrayList<>();        triplet1.add(-1);        triplet1.add(-1);        triplet1.add(2);        expectedOutput.add(triplet1);        List<Integer> triplet2 = new ArrayList<>();        triplet2.add(-1);        triplet2.add(0);        triplet2.add(1);        expectedOutput.add(triplet2);        assertEquals(expectedOutput,actual);    }}