package day6;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;import java.util.List;import static org.junit.jupiter.api.Assertions.*;class LetterCombinationsTest {    @Test    void letterCombinationTest() {        LetterCombinations combinations = new LetterCombinations();        String digits = "23";        List<String> actual = LetterCombinations.letterCombinations(digits);        String[] expected = {"ad","ae","af","bd","be","bf","cd","ce","cf"};        Assertions.assertEquals(expected,actual);    }}